import React, { useState } from "react";
import stlSB from "./SearchBar.module.css";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import Modal from "react-modal";
import stlMdl from "../SearchBar/modalStyles.module.css";
// import validCountries from "../../vallidations/contries.js";

const SearchBar = () => {
  const allCountries = useSelector((state) => state.countries);
  const navigate = useNavigate();
  const [searchInput, setSearchInput] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [suggestions, setSuggestions] = useState([]);

  const handleSearch = () => {
    const countryById = allCountries.find(
      (country) => country.id === searchInput
    );

    if (countryById) {
      navigate(`/detail/${countryById.id}`);
    } else {
      const countryByName = allCountries.find(
        (country) => country.name.toLowerCase() === searchInput.toLowerCase()
      );

      if (countryByName) {
        navigate(`/detail/${countryByName.id}`);
      } else {
        setShowModal(true);
      }
    }
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchInput(value);

    // Filtra la lista de países y obtén las sugerencias para el autocompletado
    const filteredSuggestions = allCountries.filter((country) =>
      country.name.toLowerCase().includes(value.toLowerCase())
    );

    setSuggestions(filteredSuggestions);

    // Mostrar el modal solo cuando haya sugerencias disponibles
    setShowModal(filteredSuggestions.length > 0);
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchInput(suggestion.name);
    setSuggestions([]);
  };

  return (
    <div className={stlSB.gnrlContSB}>
      <input type="text" value={searchInput} onChange={handleInputChange} />
      <button onClick={handleSearch}>Find</button>

      {/* Modal para mostrar las sugerencias */}
      <Modal
        className={stlMdl.sggModal}
        isOpen={showModal}
        onRequestClose={closeModal}
        contentLabel="Country Suggestions"
      >
        <ul>
          {suggestions.map((suggestion) => (
            <li
              key={suggestion.name}
              onClick={() => handleSuggestionClick(suggestion)}
            >
              {suggestion.name}
            </li>
          ))}
        </ul>
        <button onClick={closeModal}>Close</button>
      </Modal>

      {/* Modal para mostrar el mensaje */}
      <Modal
        className={stlMdl.gnrMdl}
        isOpen={showModal}
        onRequestClose={closeModal}
        contentLabel="Country not found"
      >
        <h2>¡Country not found!</h2>
        <h3>Please enter a valid Id value or country name</h3>
        <button onClick={closeModal}>Accept</button>
      </Modal>
    </div>
  );
};

export default SearchBar;
