import {
  GET_COUNTRIES,
  SET_SELECTED_CONTINENT,
  SET_SORT_TYPE,
  SEARCH_COUNTRY,
} from "../actions/index";

const inicitalState = {
  countries: [],
  selectedContinent: "All",
  sortType: "ascendantAlf",
  searchResult: null,
};

const rootReducer = (state = inicitalState, action) => {
  switch (action.type) {
    case GET_COUNTRIES:
      return { ...state, countries: action.payload };

    case SET_SELECTED_CONTINENT:
      return {
        ...state,
        selectedContinent: action.payload,
      };

    case SET_SORT_TYPE:
      return {
        ...state,
        sortType: action.payload,
      };

    case SEARCH_COUNTRY:
      const searchParam = action.payload;
      const foundById = state.countries.find(
        (country) => country.id === searchParam
      );
      if (foundById) {
        return {
          ...state,
          searchResult: foundById,
        };
      }

      const foundByName = state.countries.find(
        (country) => country.name.toLowerCase() === searchParam.toLowerCase()
      );
      if (foundByName) {
        return {
          ...state,
          searchResult: foundByName,
        };
      }

      return {
        ...state,
        searchResult: null,
      };

    default:
      return { ...state };
  }
};

export default rootReducer;
