import React, { useState } from "react";
import NavBar from "../../components/NavBar/NavBar";
import Footer from "../../components/Footer/Footer";
import stlCreate from "./Create.module.css";
import { Link } from "react-router-dom";

const Create = () => {

  const [formData, setFormData] = useState({
    tour: "",
    difficult: "Level 1",
    season: "Spring",
    countries: [],
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:3001/activities/", formData)
      .then((response) => {
        // Aquí puedes manejar la respuesta si es necesario
        console.log("Actividad creada exitosamente:", response.data);
      })
      .catch((error) => {
        // Aquí puedes manejar los errores si ocurre alguno
        console.error("Error al crear la actividad:", error);
      });
  };
  
  return (
    <div className={stlCreate.gnrlContCT}>
      <NavBar />

      <form className={stlCreate.formCont} onSubmit={handleSubmit}>
        <h1>CREATE A TOUR</h1>

        <div className={stlCreate.nameBasic}>
          <label htmlFor="tour">Tour Name:</label>
          <input type="text" name="tour" placeholder="name of tour..." />
        </div>

        <div className={stlCreate.itemInput}>
          <label>Difficult:</label>
          <select name="difficult" value={formData.difficult} onChange={handleChange}>
            <option>Level 1</option>
            <option>Level 2</option>
            <option>Level 3</option>
            <option>Level 4</option>
            <option>Level 5</option>
          </select>
          <Link>
            <span>Definition of Difficult</span>
          </Link>
        </div>

        <div className={stlCreate.itemInput}>
          <label htmlFor="season">Season:</label>
          <select name="season" value={formData.season} onChange={handleChange}>
            <option>Spring</option>
            <option>Summer</option>
            <option>Autumn</option>
            <option>Winter</option>
          </select>
        </div>

        <div className={stlCreate.itemInput}>
          <label htmlFor="countries">Countries:</label>
          <button>Add country or Countries</button>
        </div>
        <button>Submit</button>
      </form>

      <Footer />
    </div>
  );
};

export default Create;
