import React, { useState } from "react";
import { connect } from "react-redux"; // Importa connect desde react-redux
import { setSelectedCountries } from "../../redux/actions/index";

import { continents, validCountries } from "../../vallidations/contries"; // Actualizamos el nombre de la variable.
import stlSC from "./SelectCountries.module.css";

const SelectCountries = ({ selectedCountriesProp, setselectedCountries }) => {
  const [selectedContinents, setSelectedContinents] = useState([]);
  const [selectedCountries, setSelectedCountries] = useState([]);

  const handleContinentChange = (continent) => {
    if (selectedCountriesProp.includes(continent)) {
      setSelectedContinents(selectedCountriesProp.filter((c) => c !== continent));
    } else {
      setSelectedContinents([...selectedCountriesProp, continent]);
    }
  };

  const handleCountryChange = (country) => {
    if (selectedCountries.includes(country)) {
      setselectedCountries(selectedCountries.filter((c) => c !== country));
    } else {
      setselectedCountries([...selectedCountries, country]);
    }
  };

  console.log(selectedCountries);

  const filteredCountries = validCountries.filter(
    (country) =>
      selectedContinents.length === 0 ||
      selectedContinents.includes(country.continent)
  );

  return (
    <div className={stlSC.gnrCont}>
      <div className={stlSC.divContinents}>
        <h2>Continents</h2>
        <div className={stlSC.divContinentsUnit}>
          {continents.map((continent) => (
            <div className={stlSC.boxContinents}>
              <input
                type="checkbox"
                checked={selectedContinents.includes(continent)}
                onChange={() => handleContinentChange(continent)}
              />
              <label key={continent}>{continent}</label>
            </div>
          ))}
        </div>
      </div>

      <div className={stlSC.divCountries}>
        <h2>Countries</h2>
        <div className={stlSC.countBox}>
          {filteredCountries.map((country, index) => (
            <div className={stlSC.miniBox} key={index}>
              <input
                type="checkbox"
                checked={selectedCountries.includes(country.name)}
                onChange={() => handleCountryChange(country.name)}
              />
              <label>{country.name}</label>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  selectedCountriesProp: state.selectedCountries, // Mapea el estado global a las props
});

const mapDispatchToProps = {
  setselectedCountries, // Mapea la acción setselectedCountries a las props
};

export default connect(mapStateToProps, mapDispatchToProps)(SelectCountries);
