import React, { useEffect } from "react";
import { connect, useSelector  } from "react-redux";
import { useParams, useLocation } from "react-router-dom";
import stlDetail from "./Detail.module.css";
import { Link } from "react-router-dom";
import NavBar from "../../components/NavBar/NavBar";
import Footer from "../../components/Footer/Footer";
import { getCountries } from "../../redux/actions/index";

const Detail = (props) => {
  const { id } = useParams();
  const allCountries = useSelector((state) => state.countries);
  const location = useLocation();
  const searchResult = useSelector((state) => state.searchResult);

  console.log(allCountries);
  console.log(id);

  const countryFound = searchResult || allCountries.find((country) => country.id === id);

  console.log(countryFound);

  // Comprobar si se encontró el país
  if (!countryFound) {
    return <div>País no encontrado</div>;
  }

  // Extraer las propiedades del país encontrado
  const { name, continent, capital, subregion, lat, long, area, population, flag } =
    countryFound;

  return (
    <div>
      <NavBar />

      <div className={stlDetail.detailCont}>
        <div className={stlDetail.nameidCont}>
          <h1>Country: {name}</h1>
        </div>

        <div className={stlDetail.coutryDetail}>
          <div className={stlDetail.infoCont}>
            <div>
              <h2>Id: {id}</h2>
              <h2>Area: {area.toLocaleString()} Km²</h2>
              <h2>Capital: {capital}</h2>
              <h2>Continent: {continent}</h2>
              <h2>Subregion: {subregion}</h2>
              <h2>Population: {population}</h2>
            </div>
          </div>

          <div className={stlDetail.flagCont}>
            <img src={flag} alt="" />
            <Link to="/create">
              <button>Go Travel!</button>
            </Link>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

const mapStateToProps = (state) => {
  return {
    countries: state.countries,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getCountries: () => dispatch(getCountries()),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Detail);
