import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries, getActivities, setSelectedActivity  } from "../../redux/actions/index";
import Country from "../Country/Country";
import stlCountries from "../Countries/Countries.module.css";

const Countries = () => {
  const allCountries = useSelector((state) => state.countries);
  const selectedContinent = useSelector((state) => state.selectedContinent);
  const selectedSortType = useSelector((state) => state.sortType); // Obtener el tipo de ordenamiento
  const activities = useSelector((state) => state.activities.findAllActivities);
  const selectedActivity = useSelector((state) => state.selectedActivity);
  const dispatch = useDispatch();
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    dispatch(getCountries());
    dispatch(getActivities());
  }, []);

  const countriesPerPage = 10;

  // Filtrar los países según el continente seleccionado y la actividad seleccionada
  const filteredCountries = allCountries.filter((country) => {
    const continentMatches = selectedContinent === "All" || country.continent === selectedContinent;
    const activityMatches = selectedActivity === "" || country.activities.includes(selectedActivity);
    return continentMatches && activityMatches;
  });

  // Realizar el ordenamiento de los países según el tipo seleccionado
  const sortedCountries = [...filteredCountries]; // Crear una copia para no modificar el estado original

  switch (selectedSortType) {
    case "ascendantAlf":
      sortedCountries.sort((a, b) => a.name.localeCompare(b.name));
      break;
    case "descendingAlf":
      sortedCountries.sort((a, b) => b.name.localeCompare(a.name));
      break;
    case "asendingPob":
      sortedCountries.sort((a, b) => a.population - b.population);
      break;
    case "descendingPob":
      sortedCountries.sort((a, b) => b.population - a.population);
      break;
    default:
      break;
  }

  const totalPages = Math.ceil(sortedCountries.length / countriesPerPage);

  const handleNext = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePrevious = () => {
    if (currentPage > 0) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  const handleActivityChange = (event) => {
    const selectedActivity = event.target.value;
    dispatch(setSelectedActivity(selectedActivity));
    // Reset the current page to the first page when changing the activity filter
    setCurrentPage(0);
  };

  return (
    <div>
      <div className={stlCountries.CountriesGnrl}>
        {sortedCountries
          .slice(
            currentPage * countriesPerPage,
            (currentPage + 1) * countriesPerPage
          )
          .map((c) => (
            <Country
              key={c.id}
              id={c.id}
              name={c.name}
              flag={c.flag}
              region={c.subregion}
              population={c.population}
            />
          ))}
      </div>

      <div>
        <button className={stlCountries.btnprev} onClick={handlePrevious} disabled={currentPage === 0}>
          ←
        </button>
        <button className={stlCountries.btnpot}>Page {currentPage + 1} of {totalPages}</button>
        <button className={stlCountries.btnnext} onClick={handleNext} disabled={currentPage === totalPages - 1}>
          →
        </button>
      </div>
    </div>
  );
};

export default Countries;
