import { GET_COUNTRIES } from "../actions/index";

const inicitalState = {
  countries: [],
};

const rootReducer = (state = inicitalState, action) => {
  switch (action.type) {
    case GET_COUNTRIES:
      return { ...state, countries: action.payload };
      
    default:
      return { ...state };
  }
};

export default rootReducer;
