import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries } from "../../redux/actions/index";
import Country from "../Country/Country";
import stlCountries from "../Countries/Countries.module.css";

const Countries = () => {
  const allCountries = useSelector((state) => state.countries);
  const selectedContinent = useSelector((state) => state.selectedContinent);
  const dispatch = useDispatch();
  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    dispatch(getCountries());
  }, []);

  const countriesPerPage = 10;

  // Filtrar los países según el continente seleccionado
  const filteredCountries = selectedContinent === "All"
    ? allCountries
    : allCountries.filter(c => c.continent === selectedContinent);

  const totalPages = Math.ceil(filteredCountries.length / countriesPerPage);

  const handleNext = () => {
    if (currentPage < totalPages - 1) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePrevious = () => {
    if (currentPage > 0) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <div>
      {/* Aquí podrías colocar el componente Filter para mostrar el selector de continentes */}
      {/* <Filter /> */}

      <div className={stlCountries.CountriesGnrl}>
        {filteredCountries
          .slice(
            currentPage * countriesPerPage,
            (currentPage + 1) * countriesPerPage
          )
          .map((c) => (
            <Country
              key={c.id}
              id={c.id}
              name={c.name}
              flag={c.flag}
              region={c.subregion}
            />
          ))}
      </div>
      <div className={stlCountries.pagCont}>
        <button onClick={handlePrevious} disabled={currentPage === 0}>
          ←
        </button>
        <button>{currentPage + 1}</button>
        <button onClick={handleNext} disabled={currentPage === totalPages - 1}>
          →
        </button>
      </div>
    </div>
  );
};

export default Countries;
