import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCountries } from "../../redux/actions/index";

const Countries = () => {
  const countries = useSelector((state) => state.countries);
  const dispatch = useDispatch();

  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    dispatch(getCountries());
  }, []);

  //aqui paginado
  const handleNext = () => {
  
    if (currentPage < countries.length / 2 - 1) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePrevious = () => {
    
    if (currentPage > 0) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <div>
      <h1>Cards Func</h1>
      <div>
        {/* Mostramos los dos personajes correspondientes a la pÃ¡gina actual */}
        {countries
          .slice(currentPage * 2, currentPage * 2 + 2)
          .map((country) => (
            <div key={country.id}>
              <h2>{country.name}</h2>
              <p>{country.region}</p>
            </div>
          ))}
      </div>
      <div>
        <button onClick={handlePrevious} disabled={currentPage === 0}>
          Previous
        </button>
        <button onClick={handleNext} disabled={currentPage === countries.length / 2 - 1}>
          Next
        </button>
      </div>
    </div>
  );
};

export default Countries;
