import { GET_ALL_COUNTRIES } from "../action-types";

const initialState = {
  countries: [],
  allCountries: [],
};

export default function rootReducer(state = initialState, action) {
  switch (action.type) {
    case GET_ALL_COUNTRIES:
      return {
        ...state,
        countries: action.payload,
        allCountries: action.payload,
      };
    default:
      return state;
  }
}
