import React, { useState, useEffect } from "react";
import axios from "axios";
import NavBar from "../../components/NavBar/NavBar";
import Footer from "../../components/Footer/Footer";
import stlCreate from "./Create.module.css";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import countriesList from "../../listas/countriesList";

const Create = () => {
  const [formData, setFormData] = useState({
    tour: "",
    difficult: "Level 1",
    season: "Spring",
    countries: [],
  });

  const [showModal, setShowModal] = useState(false);
  const [selectedCountries, setSelectedCountries] = useState({});

  useEffect(() => {
    // Cargar el estado almacenado en localStorage al cargar el componente
    const storedCountries = localStorage.getItem("selectedCountries");
    if (storedCountries) {
      setSelectedCountries(JSON.parse(storedCountries));
    }
  }, []);

  useEffect(() => {
    // Guardar el estado de los países seleccionados en localStorage
    localStorage.setItem("selectedCountries", JSON.stringify(selectedCountries));
  }, [selectedCountries]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handleSelectCountries = (e) => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setSelectedCountries((prevSelectedCountries) => ({
      ...prevSelectedCountries,
      [name]: checked,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const selectedCountriesArray = Object.keys(selectedCountries).filter(
      (country) => selectedCountries[country]
    );

    setFormData((prevFormData) => ({
      ...prevFormData,
      countries: selectedCountriesArray,
    }));

    axios
      .post("http://localhost:3001/activities/", formData)
      .then((response) => {
        // Aquí puedes manejar la respuesta si es necesario
        console.log("Actividad creada exitosamente:", response);
      })
      .catch((error) => {
        // Aquí puedes manejar los errores si ocurre alguno
        console.error("Error al crear la actividad:", error);
      });
  };

  return (
    <div className={stlCreate.gnrlContCT}>
      <NavBar />

      <form className={stlCreate.formCont} onSubmit={handleSubmit}>
        <h1>CREATE A TOUR</h1>

        <div className={stlCreate.nameBasic}>
          <label htmlFor="tour">Tour Name:</label>
          <input
            type="text"
            name="tour"
            placeholder="name of tour..."
            value={formData.tour}
            onChange={handleChange}
          />
        </div>

        <div className={stlCreate.itemInput}>
          <label>Difficult:</label>
          <select
            name="difficult"
            value={formData.difficult}
            onChange={handleChange}
          >
            <option>Level 1</option>
            <option>Level 2</option>
            <option>Level 3</option>
            <option>Level 4</option>
            <option>Level 5</option>
          </select>
          <Link>
            <span>Definition of Difficult</span>
          </Link>
        </div>

        <div className={stlCreate.nameBasic}>
          <label htmlFor="duration">Duration of tour:</label>
          <input
            type="text"
            name="duration"
            placeholder="Duration of the tour..."
            value={formData.duration}
            onChange={handleChange}
          />
        </div>

        <div className={stlCreate.itemInput}>
          <label htmlFor="season">Season:</label>
          <select name="season" value={formData.season} onChange={handleChange}>
            <option>Spring</option>
            <option>Summer</option>
            <option>Autumn</option>
            <option>Winter</option>
          </select>
        </div>

        <div className={stlCreate.itemInput}>
          <label htmlFor="countries">Countries:</label>
          <button type="button" onClick={handleSelectCountries}>
            Add country or Countries
          </button>

          <Modal
            isOpen={showModal}
            onRequestClose={closeModal}
            contentLabel="Select countries"
          >
            <h2>¡Select countries!</h2>
            {countriesList.map((country) => (
              <label key={country}>
                <input
                  type="checkbox"
                  name={country}
                  checked={selectedCountries[country] || false}
                  onChange={handleCheckboxChange}
                />
                {country}
              </label>
            ))}
            
            <button onClick={closeModal}>Add</button>
          </Modal>
        </div>

        <button type="submit">Submit</button>
      </form>

      <Footer />
    </div>
  );
};

export default Create;
