import React from "react";
import stlCreate from "./countryModel.module.css";

const CountryModal = ({ countries, selectedCountries, onClose, onSave }) => {
  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      onSave([...selectedCountries, value]);
    } else {
      onSave(selectedCountries.filter((country) => country !== value));
    }
  };

  return (
    <div className={stlCreate.modalOverlay}>
      <div className={stlCreate.modalContent}>
        <h2>Select 10 Countries</h2>
        <div>
          {countries.map((country) => (
            <label key={country}>
              <input
                type="checkbox"
                value={country}
                checked={selectedCountries.includes(country)}
                onChange={handleCheckboxChange}
              />
              {country}
            </label>
          ))}
        </div>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default CountryModal;
