import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { setCountriesToCreate } from '../../redux/actions/index';
import { continents, validCountries } from "../../vallidations/contries"; // Actualizamos el nombre de la variable.
import stlSC from "./SelectCountries.module.css";

const SelectCountries = () => {
  const [selectedContinents, setSelectedContinents] = useState([]);

  const dispatch = useDispatch();
  

  const handleContinentChange = (continent) => {
    if (selectedContinents.includes(continent)) {
      setSelectedContinents(selectedContinents.filter((c) => c !== continent));
    } else {
      setSelectedContinents([...selectedContinents, continent]);
    }
  };

  const handleCountryChange = (event) => {
    const { value } = event.target;
    dispatch(setCountriesToCreate(value)); // Envia la acción para actualizar selectedCountries en el store
  };

  dispatch(setCountriesToCreate);

  const filteredCountries = validCountries.filter(
    (country) =>
      selectedContinents.length === 0 ||
      selectedContinents.includes(country.continent)
  );

  return (
    <div className={stlSC.gnrCont}>
      <div className={stlSC.divContinents}>
        <h2>Continents</h2>
        <div className={stlSC.divContinentsUnit}>
          {continents.map((continent) => (
            <div className={stlSC.boxContinents}>
              <input
                  type="checkbox"
                  checked={selectedContinents.includes(continent)}
                  onChange={() => handleContinentChange(continent)}
                />
              <label key={continent}>
                {continent}
              </label>
            </div>
          ))}
        </div>
      </div>

      <div className={stlSC.divCountries}>
        <h2>Countries</h2>
        <div className={stlSC.countBox}>
          {filteredCountries.map((country) => (
            <div className={stlSC.miniBox}>
              <input
                type="checkbox"
                onChange={() => handleCountryChange(country.name)}
              />
              <label key={country.name}>{country.name}</label>
            </div>
          ))}
        </div>

      </div>
    </div>
  );
};

export default SelectCountries;
