//Common
import Country from "../Country/Country";
import { getCountries } from '../../redux/actions/index';

//React
import React, { useEffect, useState } from "react";

//React-Redux
import { useDispatch, useSelector } from "react-redux";

//Redux


//Components

//CSS
import stlCountries from "../Countries/Countries.module.css";

const Countries = () => {

  const countries = useSelector((state) => state.countries);
  const dispatch = useDispatch();

  const [currentPage, setCurrentPage] = useState(0);

  useEffect(() => {
    dispatch(getCountries());
  }, []);

  //aqui paginado
  const handleNext = () => {
    // Avanzamos a la siguiente página solo si no estamos en la última página
    if (currentPage < countries.length / 2 - 1) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePrevious = () => {
    // Retrocedemos a la página anterior solo si no estamos en la primera página
    if (currentPage > 0) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  return (
    <div className={stlCountries.CountriesGnrl}>
      <div>
        {/* Mostramos los dos personajes correspondientes a la página actual */}
        {countries
          .slice(currentPage * 2, currentPage * 2 + 2)
          .map((c) => (
            <div key={c.id}>
              <Country key={c.cca3} id={c.cca3} name={c.name.common} flag={c.flag[1]} region={c.region} />
            </div>
          ))}
      </div>
      <div>
        <button onClick={handlePrevious} disabled={currentPage === 0}>
          Previous
        </button>
        <button onClick={handleNext} disabled={currentPage === countries.length / 10 - 1}>
          Next
        </button>
      </div>
    </div>
  );
};

export default Countries;
