import React, { useState } from 'react';
import stlSB from './SearchBar.module.css';
import { useSelector  } from "react-redux";
import { useHistory } from 'react-router-dom';
import Modal from 'react-modal';

const SearchBar = () => {
  const allCountries = useSelector((state) => state.countries);
  const history = useHistory();

  console.log(allCountries);

  const [searchInput, setSearchInput] = useState('');
  const [showModal, setShowModal] = useState(false);

  const handleSearch = () => {
    // Buscar el país por ID (searchTerm)
    const countryById = allCountries.find((country) => country.id === searchTerm);

    if (countryById) {
      // Si se encuentra el país por ID, redirigir a la página de detalle
      history.push(`/detail/${countryById.id}`);
    } else {
      // Si no se encuentra el país por ID, buscar por nombre
      const countryByName = allCountries.find(
        (country) => country.name.toLowerCase() === searchTerm.toLowerCase()
      );

      if (countryByName) {
        // Si se encuentra el país por nombre, redirigir a la página de detalle
        history.push(`/detail/${countryByName.id}`);
      } else {
        // Si no se encuentra el país por nombre, mostrar el modal
        setShowModal(true);
      }
    }
  };

  const closeModal = () => {
    // Función para cerrar el modal
    setShowModal(false);
  };

  return (
    <div className={stlSB.gnrlContSB}>
        <input
          type="text"
          value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}
        />
        <button onClick={handleSearch}>Find</button>
    </div>
  )
}

export default SearchBar
